mapper.select:
select rownum, n.* 
from (
	select * from bBoard 
	where board_category = #{category} 
	order by board_num desc
) n
where rownum between #{begin} and #{end}

select rownum, n.*
from (
	select * from traffic
	order by tra_num desc
) n
where rownum between #{begin} and #{end}

select user_imagepath
from bUser
where user_email = #{email}
